pragma solidity ^0.4.24;

import '../Libraries/SafeMath.sol';
import '../Libraries/Math.sol';
import '../Libraries/RLP.sol';
import '../Libraries/Merkle.sol';
import '../Libraries/Validate.sol';
import '../DataStructures/PriorityQueue.sol';
import "zeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "zeppelin-solidity/contracts/ownership/Ownable.sol";
import "zeppelin-solidity/contracts/token/ERC20/SafeERC20.sol";

/**
 * @title RootChain
 * @dev This contract secures a utxo payments plasma child chain to ethereum
 */

contract RootChain is Ownable {
    using SafeMath for uint256;
    using Merkle for bytes32;
    using RLP for bytes;
    using RLP for RLP.RLPItem;
    using RLP for RLP.Iterator;
    using SafeERC20 for ERC20;

    /*
     * Events
     */
    event Deposit(
        address indexed depositor,
        uint256 indexed blockNumber,
        uint256 indexed depositBlock,
        address token,
        uint256 amount
    );

    event ExitStarted(
        address indexed exitor,
        uint256 indexed blockNumber,
        uint256 indexed utxoPos,
        address token,
        uint256 amount
    );

    event ExitChallenged(
        address indexed challenger,
        uint256 indexed blockNumber,
        uint256 indexed cUtxoPos,
        uint256 eUtxoPos
    );

    event ExitFinalized(
        address indexed exitor,
        uint256 indexed blockNumber,
        uint256 indexed utxoPos,
        address token,
        uint256 amount
    );

    /*
     *  Storage
     */
    mapping(uint256 => childBlock) public childChain;
    mapping(uint256 => exit) public exits;
    PriorityQueue exitsQueue;
    address public authority;
    /* Block numbering scheme below is needed to prevent Ethereum reorg from invalidating blocks submitted
       by operator. Two mechanisms must be in place to prevent chain from crashing:
       1) don't mine tx that spent fresh deposits; if they are reorged from existence, block is invalid
       2) disappearance of submit block does not affect operator's block numbering; hence tx submitted by
       users that address that block stay valid.
    */
    mapping(uint256 => uint256) public childDepositBlock;
    uint256 public currentChildBlock; /* ends with 00000 */
    //uint256 public currentDepositBlock; /* takes values in range 1..99999 */
    uint256 public childBlockInterval;
    // uint256 public currentFeeExit;
    // uint256 public lastParentBlock;

    struct exit {
        address owner;
        ERC20 token;
        uint256 amount;
    }

    struct childBlock {
        // uint8 block_type;
        bytes32 root;
        uint256 created_at;
    }

    /*
     *  Modifiers
     */
    modifier isAuthority() {
        require(msg.sender == authority);
        _;
    }

    /*
     * Public Functions
     */

    constructor()
        public
    {
        authority = msg.sender;
        childBlockInterval = 100000;
        currentChildBlock = childBlockInterval;
        // childDepositBlock[currentChildBlock] = 1;
        // currentDepositBlock = 1;
        // currentFeeExit = 1;
        exitsQueue = new PriorityQueue();
        // lastParentBlock = block.number;
    }

    // @dev Allows assigning new authority
    // @param _authority The new authority
    function setAuthority(address _authority)
        public
        isAuthority
    {   
        authority = _authority;
    }

    // @dev Allows Plasma chain operator to submit block root
    // @param root The root of a child chain block
    function submitBlock(uint256 blkNum, bytes32 root)
        public
        isAuthority
    {
        // ensure finality on previous blocks before submitting another
        // require(block.number >= lastParentBlock.add(6), "presumed finality required");
        require(childChain[blkNum].root == 0, "already submitted");
        require(currentChildBlock == blkNum, "submit wrong block");

        childChain[blkNum] = childBlock({
            root: root,
            created_at: block.timestamp
        });
        currentChildBlock = currentChildBlock.add(childBlockInterval);
        // childDepositBlock[currentChildBlock] = 1;
        // currentDepositBlock = 1;
        // lastParentBlock = block.number;
    }

    // @dev Allows anyone to deposit funds into the Plasma chain
    // txBytes Length 12 RLP encoding of Transaction excluding signatures
    // Transaction encoding:
    // [Blknum1, TxIndex1, Oindex1,
    //  Blknum2, TxIndex2, Oindex2,
    //  NewOwner, Denom1, NewOwner, Denom2, Fee, Token]
    // @param blkNum The block number to deposit into
    // @param txBytes The format of the transaction that'll become the deposit
    function deposit(uint256 blkNum, ERC20 token, uint256 amount)
        public
        payable
    {   
        require(blkNum % childBlockInterval == 0, "blockNumber has to end with 00000");
        require(blkNum >= currentChildBlock - childBlockInterval, "blkNum is greater than or equal to current child block");
        if (childDepositBlock[blkNum] == 0){
            childDepositBlock[blkNum] = 1;
        }
        uint256 cDepositBlock = childDepositBlock[blkNum];
        require(cDepositBlock < childBlockInterval);

        // var depositTx = txBytes.createDepositTx(12);
        // uint256 amount = depositTx.amount;
        // ERC20 token = ERC20(depositTx.token);
        if (address(token) == address(0)){
            require(msg.value == amount, "mismatch in value");
        }else{
            token.safeTransferFrom(msg.sender, address(this), amount);
        }

        // bytes32 root = keccak256(keccak256(txBytes), new bytes(130));
        bytes32 root = keccak256(msg.sender, token, amount);
        uint256 depositBlock = blkNum.add(cDepositBlock);
        childChain[depositBlock] = childBlock({
            root: root,
            created_at: block.timestamp
        });
        childDepositBlock[blkNum] = childDepositBlock[blkNum].add(1);
        //currentDepositBlock = currentDepositBlock.add(1);
        emit Deposit(msg.sender, blkNum, depositBlock, token, amount);
    } 

    function deposit(ERC20 token, uint256 amount)
        public
        payable
    {   
        deposit(getLastestSubmittedChildBlock(), token, amount);
    }

    // function startDepositExit(uint256 depositPos, ERC20 token, uint256 amount)
    //     public
    // {
    //     uint256 depositBlock = depositPos / 1000000000;
    //     // Makes sure that deposit position is actually a deposit
    //     require(depositBlock % childBlockInterval != 0);
    //     bytes32 root = childChain[depositBlock].root;
    //     bytes32 depositHash = keccak256(msg.sender, token, amount);
    //     require(root == depositHash);
    //     addExitToQueue(depositPos, msg.sender, token, amount, childChain[depositBlock].created_at);
    // }

    // function startFeeExit(ERC20 token, uint256 amount)
    //     public
    //     isAuthority
    //     returns (uint256)
    // {
    //     addExitToQueue(currentFeeExit, msg.sender, token, amount, block.timestamp + 1);
    //     currentFeeExit = currentFeeExit.add(1);
    // }

    // @dev Starts to exit a specified utxo
    // @param utxoPos The position of the exiting utxo in the format of blkNum * 1000000000 + index * 10000 + oIndex
    // @param txBytes The transaction being exited in RLP bytes format
    // @param proof Proof of the exiting transactions inclusion for the block specified by utxoPos
    // @param sigs Both transaction signatures and confirmations signatures used to verify that the exiting transaction has been confirmed
    event Debug(address sender, address exitor);
    // event LDebug(bytes32 confirmationHash, bytes confSig1, bytes txBytes, bytes32 root, address owner1, address owner2);

    function startExit(uint256 utxoPos, bytes txBytes, bytes proof, bytes sigs)
        public returns (uint256 eUtxoPos)
    {
        uint256 blkNum = utxoPos / 1000000000;
        uint256 txIndex = (utxoPos % 1000000000) / 10000;
        uint256 oIndex = utxoPos - blkNum * 1000000000 - txIndex * 10000; 
        var txList = txBytes.toRLPItem().toList();
        require(txList.length == 12);

        address exitor = txList[6+oIndex*2].toAddress();
        address token = txList[11].toAddress();
        uint256 amount = txList[7+oIndex*2].isEmpty() ? 0 : txList[7+oIndex*2].toUint();
        uint256 inputCount = (txList[0].isEmpty() ? 0 : txList[0].toUint()) * (txList[3].isEmpty() ? 0 :txList[3].toUint());
        emit Debug(msg.sender, exitor);
        require(msg.sender == exitor || msg.sender == owner, "sender and exitor are not the same or sender is not the owner");

        bytes32 merkleHash = keccak256(keccak256(txBytes), ByteUtils.slice(sigs, 0, 130));
        // bytes memory sig1 = ByteUtils.slice(sigs, 0, 65);
        // bytes memory sig2 = ByteUtils.slice(sigs, 65, 65);
        // bytes memory confSig1 = ByteUtils.slice(sigs, 130, 65);
        // bytes32 confirmationHash = keccak256(keccak256(txBytes), childChain[blkNum].root);
        // bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        // address owner1 = ECRecovery.recover(keccak256(prefix, keccak256(txBytes)), sig1);
        // address owner2 = ECRecovery.recover(keccak256(prefix, confirmationHash), confSig1);
        // emit LDebug(confirmationHash, confSig1, txBytes, childChain[blkNum].root, owner1, owner2);

        require(Validate.checkSigs(keccak256(txBytes), childChain[blkNum].root, inputCount, sigs, owner), "signature is invalid");
        require(merkleHash.checkMembership(childChain[blkNum].root, proof), "proof is invalid");
        addExitToQueue(utxoPos, exitor, ERC20(token), amount, childChain[blkNum].created_at);
        return utxoPos;
    }

    // Priority is a given utxos position in the exit priority queue
    function addExitToQueue(uint256 utxoPos, address exitor, ERC20 token, uint256 amount, uint256 created_at)
        private
    {
        // uint256 blkNum = utxoPos / 1000000000;
        uint256 exitable_at = Math.max(created_at + 2 weeks, block.timestamp + 1 weeks);
        uint256 priority = exitable_at << 128 | utxoPos;
        require(amount > 0);
        require(exits[utxoPos].amount == 0);
        exitsQueue.insert(priority);
        exits[utxoPos] = exit({
            owner: exitor,
            token: token,
            amount: amount
        });
        emit ExitStarted(msg.sender, getLastestSubmittedChildBlock(), utxoPos, token, amount);
    }

    // @dev Allows anyone to challenge an exiting transaction by submitting proof of a double spend on the child chain
    // @param cUtxoPos The position of the challenging utxo
    // @param eUtxoPos The position of the exiting utxo
    // @param txBytes The challenging transaction in bytes RLP form
    // @param proof Proof of inclusion for the transaction used to challenge
    // @param sigs Signatures for the transaction used to challenge
    // @param confirmationSig The confirmation signature for the transaction used to challenge
    function challengeExit(uint256 cUtxoPos, uint256 eUtxoPos, bytes txBytes, bytes proof, bytes sigs, bytes confirmationSig)
        public returns (bool success)
    {
        // require(cUtxoPos != eUtxoPos, "eUtxoPos and cUtxoPos should not be the same");

        uint256 txIndex = (cUtxoPos % 1000000000) / 10000;
        bytes32 root = childChain[cUtxoPos / 1000000000].root;
        var txHash = keccak256(txBytes);
        var confirmationHash = keccak256(txHash, root);
        var merkleHash = keccak256(txHash, ByteUtils.slice(sigs, 0, 130));


        require(exits[eUtxoPos].token == txBytes.toRLPItem().toList()[11].toAddress(), "tokens are not the same");
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";

        require(exits[eUtxoPos].owner == ECRecovery.recover(keccak256(prefix, confirmationHash), confirmationSig), "this tx is not spent by the owner");
        require(merkleHash.checkMembership(root, proof), "this tx is not include in the submitted block");
        // Clear as much as possible from succesful challenge
        if(isExitSpent(txBytes, eUtxoPos)){
            delete exits[eUtxoPos];
            emit ExitChallenged(msg.sender, getLastestSubmittedChildBlock(), cUtxoPos, eUtxoPos);
            return true;
        }
        return false;
    }

    function isExitSpent(bytes txBytes, uint256 utxoPos)
        view
        internal
        returns (bool spent)
    {
        uint256 cblkNum = utxoPos / 1000000000;
        uint256 ctxIndex = (utxoPos % 1000000000) / 10000;
        uint256 coIndex = utxoPos - cblkNum * 1000000000 - ctxIndex * 10000; 

        var txList = txBytes.toRLPItem().toList();
        require(txList.length == 12);

        // Check two inputs individually if it spent the given withdrawal.
        for (uint256 i = 0; i < 2; i++) {
            if (!txList[3 * i].isEmpty()) {
                uint256 blkNum = txList[3 * i].toUint();
                // RLP will encode integer 0 to 0x80 just like empty content...
                uint256 txIndex = txList[3 * i + 1].isEmpty() ? 0 : txList[3 * i + 1].toUint();
                uint256 oIndex = txList[3 * i + 2].isEmpty() ? 0 : txList[3 * i + 2].toUint();

                if (cblkNum == blkNum &&
                    ctxIndex == txIndex &&
                    coIndex == oIndex) {
                    return true;
                }
            }
        }
        return false;
    }

    // @dev Loops through the priority queue of exits, settling the ones whose challenge
    // @dev challenge period has ended
    function finalizeExits()
        public
    {
        uint256 utxoPos;
        uint256 exitable_at;
        (utxoPos, exitable_at) = getNextExit();
        exit memory currentExit = exits[utxoPos];
        while (exitable_at < block.timestamp && exitsQueue.currentSize() > 0) {
            currentExit = exits[utxoPos];
            ERC20 token = currentExit.token;
            if(address(token) == address(0)){
                currentExit.owner.transfer(currentExit.amount);
            }else{
                token.safeTransfer(currentExit.owner, currentExit.amount);
            }
            exitsQueue.delMin();
            delete exits[utxoPos];

            emit ExitFinalized(currentExit.owner, getLastestSubmittedChildBlock(), utxoPos, token, currentExit.amount);
            if (exitsQueue.currentSize() > 0) {
                (utxoPos, exitable_at) = getNextExit();
            } else {
                return;
            }
        }
    }

    /* 
     *  Constant functions
     */
    function getChildChain(uint256 blockNumber)
        public
        view
        returns (bytes32, uint256)
    {
        return (childChain[blockNumber].root, childChain[blockNumber].created_at);
    }

    function getLastestSubmittedChildBlock()
        public
        view
        returns (uint256)
    {
        return currentChildBlock.sub(childBlockInterval);
    }

    function getCurrentChildBlock()
        public
        view
        returns (uint256)
    {
        return currentChildBlock;
    }

    // function getDepositBlock()
    //     public
    //     view
    //     returns (uint256)
    // {
    //     return getLastestSubmittedChildBlock().add(currentDepositBlock);
    // }

    function getExit(uint256 utxoPos)
        public
        view
        returns (address, uint256)
    {
        return (exits[utxoPos].owner, exits[utxoPos].amount);
    }

    function getNextExit()
        public
        view
        returns (uint256, uint256)
    {
        uint256 priority = exitsQueue.getMin();
        uint256 utxoPos = uint256(uint128(priority));
        uint256 exitable_at = priority >> 128;
        return (utxoPos, exitable_at);
    }

    function isFinalizable()
        public
        view
        returns (bool)
    {
        uint256 utxoPos;
        uint256 exitable_at;
        (utxoPos, exitable_at) = getNextExit();
        return exitable_at < block.timestamp && exitsQueue.currentSize() > 0;
    }
}
